}

class App extends React.Component {
  state = {
    matrix: new Array(24).fill(new Array(10).fill(0)),
    timer: new Date().toLocaleString
  }

  // useEffect(() => {
  //   let t = timer
  //   const time = setTimeout(() => t += 1, 1000)
  //   setTimer(t)
  // }, [])

  render() {
    return (
      <StyledApp>
        <div>
          {this.timer}
        </div>
        {
          this.matrix.map(row => row.map(cell => {
            if (cell === 1)
              return (
                <Block />
              )
          }))
        }
      </StyledApp>
    );
  }
}

export default App;

const StyledApp = styled.div`
  display: flex;
  flex-wrap: wrap;
  background: #61dafb;
  width: 300px;
  height: 720px;
`
