import React from 'react';
import './App.css';
import styled from 'styled-components';
import _ from 'lodash';

import Block from './components/Block'

class App extends React.Component {
  state = {
    matrix: new Array(24).fill(new Array(10).fill(0)),
    blockPos: {x: 0, y: 0}
  }

  moveBlockDown = () => {
    const {matrix, blockPos} = this.state
    let newMatrix = _.cloneDeep(matrix)

    console.log(newMatrix)
  }

  render() {

    this.moveBlockDown()
    return (
      <StyledApp>

        {
          this.state.matrix.map(row => row.map(cell => {
            if (cell === 1)
              return (
                <Block/>
              )
          }))
        }
      </StyledApp>
    );
  }
}

export default App;

const StyledApp = styled.div`
  display: flex;
  flex-wrap: wrap;
  background: #61dafb;
  width: 300px;
  height: 720px;
`
