  }

  render() {
    return (
      <StyledApp>
        <div>
          {this.state.timer}
        </div>
        {
          this.state.matrix.map(row => row.map(cell => {
            if (cell === 1)
              return (
                <Block/>
              )
          }))
        }
      </StyledApp>
    );
  }
}

export default App;

