import React from 'react';
import './App.css';
import styled from 'styled-components';
import _ from 'lodash';

import Block from './components/Block'

class App extends React.Component {
  state = {
    matrix: new Array(24).fill(new Array(10).fill(0)),
    blockPos: {x: 0, y: 0}
  }

  componentDidMount() {
    const timer = setTimeout(() => {
      this.moveBlockDown()
    }, 1000)
  }

  moveBlockDown = async () => {
    const {matrix, blockPos} = this.state

    console.log('matrix', matrix)

    let newMatrix = await _.cloneDeep(matrix)

    console.log('newMatrix before', newMatrix)

    if (blockPos.y > 0) {
      newMatrix[blockPos.y - 1][blockPos.x] = 0
    }

    newMatrix[blockPos.y][blockPos.x] = 1
    const newBlockPos = {...blockPos, y: 1}

    console.log('newMatrix after', newMatrix)

    this.setState({
      matrix: newMatrix,
      blockPos: newBlockPos
    })
  }

  render() {

    console.log(this.state)

    return (
      <StyledApp>

        {
          this.state.matrix.map((row, i) => row.map((cell, index) => {
              const background = cell === 1
              return (
                <Block key={i * index + Math.random()} background={background}/>
              )
          }))
        }
      </StyledApp>
    );
  }
}

export default App;

const StyledApp = styled.div`
  display: flex;
  flex-wrap: wrap;
  background: #61dafb;
  width: 300px;
  height: 720px;
`
