import React from 'react';
import './App.css';
import styled from 'styled-components';

import Block from './components/Block'

const quadBlock = [{x: 3, y: 0}, {x: 4, y: 0}, {x: 3, y: 1}, {x: 4, y: 1}]
const lineBlock = [{x: 4, y: 0}, {x: 4, y: 1}, {x: 4, y: 2}, {x: 4, y: 3}]
const tBlock = [{x: 4, y: 0}, {x: 4, y: 1}, {x: 3, y: 1}, {x: 5, y: 1}]
const zBlock = [{x: 3, y: 0}, {x: 4, y: 0}, {x: 4, y: 1}, {x: 5, y: 1}]
const sBlock = [{x: 4, y: 0}, {x: 3, y: 0}, {x: 3, y: 1}, {x: 2, y: 1}]
const rBlock = [{x: 3, y: 0}, {x: 3, y: 1}, {x: 2, y: 1}, {x: 1, y: 1}]
const backRBlock = [{x: 3, y: 0}, {x: 3, y: 1}, {x: 4, y: 1}, {x: 5, y: 1}]

const blocks = [quadBlock, lineBlock, tBlock, zBlock, sBlock, rBlock, backRBlock];

class App extends React.Component {
  state = {
    matrix: new Array(24).fill(new Array(10).fill(0)),
    blockPos: blocks[this.blockRandom],
    blockRandom: Math.floor(Math.random() * 8)
  }

  componentDidMount() {
    setInterval(() => {
      this.moveBlockDown()
    }, 500)
  }

  moveBlockDown = () => {
    const {matrix, blockPos} = this.state

    let newMatrix = matrix.map(row => [...row])

    if (blockPos.y > 0) {
      newMatrix[blockPos.y - 1][blockPos.x] = 0
    }

    newMatrix[blockPos.y][blockPos.x] = 1

    let newBlockPos = {...blockPos}

    if (blockPos.y >= matrix.length - 1 || newMatrix[blockPos.y + 1][blockPos.x] === 1) {
      newBlockPos = {x: 5, y: 0}
    } else {
      newBlockPos = {...blockPos, y: blockPos.y + 1}
    }

    this.setState({
      matrix: newMatrix,
      blockPos: newBlockPos
    })
  }

  render() {

    console.log(this.state)

    return (
      <StyledApp onClick={() => clearInterval()}>

        {
          this.state.matrix.map((row, i) => row.map((cell, index) => {
              const background = cell === 1
              return (
                <Block key={i * index + Math.random()} background={background}/>
              )
          }))
        }
      </StyledApp>
    );
  }
}

export default App;

const StyledApp = styled.div`
  display: flex;
  flex-wrap: wrap;
  background: #61dafb;
  width: 300px;
  height: 720px;
`
