      {
        matrix.map(row => row.map(cell => {
          if (cell === 1)
          return (
            <Block />
          )
        }))
      }
    </StyledApp>
  );
}

export default App;

const StyledApp = styled.div`
  display: flex;
  flex-wrap: wrap;
  background: #61dafb;
  width: 300px;
  height: 720px;
`
